  //  const json = await res.json()
  //  se nao, vc so retorna

  return {
    props: {
      cards: data,
      // aqui claro, eu usaria o json de alguma forma...
      // tipo json.data
      // ğŸ‘¨ğŸ»â€ğŸ’»
    },
  };
};

export default Index;
